import Galeria from "../components/Galeria";
import { useContext , useState } from "react";
import Context from "../context/Context";

export default function Favoritos(props) {
  const [listaFavoritos, setlistaFavoritos] = useContext(Context);

  const guardar = (Favoritos) => {

    const nuevoFavoritos = {

      ...Favoritos
    }
    setlistaFavoritos([...listaFavoritos, nuevoFavoritos])
    console.log(listaFavoritos)
  }

  return (

    <>
      <div>
        <h1>Fotos favoritas</h1>
        <div className="p-3 galeria grid-columns-4">
          {listaFavoritos.map((favoritos) => (
            <div className="galeria .foto" key={favoritos.id}>

              <div className="galeria2foto" style={{ backgroundImage: `url(${favoritos.src.original})` }} width={"200px"} height={"200px"}>

                {favoritos.alt}                                                                                                           f
                {favoritos.photographer}

              </div>


            </div>
          ))}

        </div>
      </div>
    </>
  );
}
